import React, { useEffect, useState } from 'react';
import { displayPerson, displayCriminal } from './apis/apis.js';

function App() {
  const [profileData, setProfileData] = useState<{ name: string, image: string } | null>(null);

  useEffect(() => {
    const fetchData = async () => {
      const randomChoice = Math.random() < 0.5 ? 'person' : 'criminal';

      if (randomChoice === 'person') {
        const data = await displayPerson();
        setProfileData(data.props);
      } else {
        const data = await displayCriminal();
        setProfileData(data.props);
      }
    };

    fetchData();
  }, [setProfileData]); // Add setProfileData to the dependency array

  return (
    <div>
      <h1>Display Random Profile</h1>

      <h2>Profile</h2>
      {profileData ? (
        <>
          {profileData.name && <h3>{profileData.name}</h3>}
          {profileData.image && <img src={profileData.image} alt={profileData.name} />}
        </>
      ) : (
        <p>No profile data available.</p>
      )}
    </div>
  );
}

export default App;
