import React, { useState } from 'react';

const GuessButtons = ({ isCriminal }) => {
    const [guess, setGuess] = useState(null);

    const handleGuess = (guessedIsCriminal) => {
        setGuess(guessedIsCriminal);
    };

    const isCorrect = guess === isCriminal;

    return (
        <div>
            <button
                style={{ backgroundColor: guess !== null && isCorrect ? 'green' : guess !== null && !isCorrect ? 'red' : 'transparent' }}
                onClick={() => handleGuess(true)}
                disabled={guess !== null}
            >
                Criminal
            </button>
            <button
                style={{ backgroundColor: guess !== null && !isCorrect ? 'green' : guess !== null && isCorrect ? 'red' : 'transparent' }}
                onClick={() => handleGuess(false)}
                disabled={guess !== null}
            >
                Not Criminal
            </button>
        </div>
    );
};

export default GuessButtons;
