import React, { useState } from 'react';

const GuessButtons = ({ isCriminal }) => {
    const [guess, setGuess] = useState(false);
    const [isCorrect, setIsCorrect] = useState(null);

    const handleGuess = (guess) => {
        setGuess(true);

        if (guess === isCriminal) {
            setIsCorrect(true);
        } else {
            setIsCorrect(false);
        }
    };

    return (
        <div>
            <button
                style={{ backgroundColor: guess && isCorrect === true ? 'green' : guess && isCorrect === false ? 'red' : 'transparent' }}
                onClick={() => handleGuess(true)}
                disabled={guess}
            >
                Criminal
            </button>
            <button
                style={{ backgroundColor: guess && isCorrect === false ? 'green' : guess && isCorrect === true ? 'red' : 'transparent' }}
                onClick={() => handleGuess(false)}
                disabled={guess}
            >
                Not Criminal
            </button>
        </div>
    );
};

export default GuessButtons;
